if(typeof require !== "undefined") {
	var TypeDefinition = require("./nixpsit-unit-TypeDefinition.js");
	var EndOfType = require("./nixpsit-unit-EndOfType.js");
	var TestObject = require("../nixpsit-unit-TestObject.js");
}

(function() {
	ParentType = function(pRequired, pCallback) {
		this.typeDefinition(pRequired);
		
		this.callback = pCallback;
	}
	
	ParentType.prototype = Object.assign({}, TypeDefinition.prototype, {
		constructor: ParentType,
		typeDefinition: TypeDefinition,
		
		getValue: function(pPointer, pTestObject) {
			if(pPointer === 0) {
				return this.callback.call(null, TestObject.copyObject(pTestObject.object), TestObject.copyObject(pTestObject.parent));
			} else {
				return new EndOfType();
			}
		}
	});
})();

if(typeof module !== "undefined") {
	module.exports = ParentType;
}