if(typeof require !== "undefined") {
	var TypeDefinition = require("./nixpsit-unit-TypeDefinition.js");
	var EndOfType = require("./nixpsit-unit-EndOfType.js");
	var StringDictionary = require("./dictionaries/nixpsit-unit-StringDictionary.js");
}

(function() {
	StringType = function(pRequired, pEmpty) {
		this.typeDefinition(pRequired);

		this.dictionary = new StringDictionary();
		this.empty = pEmpty;
	}
	
	StringType.prototype = Object.assign({}, TypeDefinition.prototype, {
		constructor: StringType,
		typeDefinition: TypeDefinition,
		
		getValue: function(pPointer, pTestObject) {
			switch(pPointer) {
				case 0: if(this.empty === true) { return ""; } else { this.pointer++; };
				case 1: return "a";
				case 2: return this.dictionary.getVariable();
				default: return new EndOfType();
			}
		}
	});
})()

if(typeof module !== "undefined") {
	module.exports = StringType;
}